  function FAQCard({ question, answer }) {
    const [isOpen, setIsOpen] = useState(false);
  
    const toggleOpen = () => {
      setIsOpen(!isOpen);
    };
  
    return (
      <div className="faq-card">
        <div className={`faq-question ${isOpen ? 'open' : ''}`} onClick={toggleOpen}>
          {question}
          <div className={`faq-icon ${isOpen ? 'open' : ''}`}>{isOpen ? '-' : '+'}</div>
        </div>
        {isOpen && <div className="faq-answer">{answer}</div>}
      </div>
    );
  }
  
  function FAQ() {
    const faqData = [
      {
        question: 'What is React?',
        answer: 'React is a JavaScript library for building user interfaces.',
      },
      {
        question: 'How do I install React?',
        answer: 'You can install React by running "npm install react" in your project directory.',
      },
      // Add more FAQ items as needed
    ];
  
    return (
      <div className="faq-container">
        <h1>Frequently Asked Questions</h1>
        <div className="faq-list">
          {faqData.map((item, index) => (
            <FAQCard key={index} question={item.question} answer={item.answer} />
          ))}
        </div>
      </div>
    );
  }
